INSERT INTO users.solutions(exercise_id, query) VALUES
(13, 'select bks.starttime from exercises.bookings bks inner join exercises.members mems on mems.memid = bks.memid where mems.firstname=''David'' and mems.surname=''Farrell'';'),
(14, 'select bks.starttime as start, facs.name as name from exercises.facilities facs inner join exercises.bookings bks on facs.facid = bks.facid where facs.name in (''Tennis Court 2'',''Tennis Court 1'') and bks.starttime >= ''2012-09-21'' and bks.starttime < ''2012-09-22'' order by bks.starttime;'),
(15, 'select distinct recs.firstname as firstname, recs.surname as surname from exercises.members mems inner join exercises.members recs on recs.memid = mems.recommendedby order by surname, firstname;'),
(16, 'select mems.firstname as memfname, mems.surname as memsname, recs.firstname as recfname, recs.surname as recsname from exercises.members mems left outer join exercises.members recs on recs.memid = mems.recommendedby order by memsname, memfname;'),
(17, 'select distinct mems.firstname || '' '' || mems.surname as member, facs.name as facility from exercises.members mems inner join exercises.bookings bks on mems.memid = bks.memid inner join exercises.facilities facs on bks.facid = facs.facid where facs.name in (''Tennis Court 2'',''Tennis Court 1'') order by member, facility;'),
(18, 'select mems.firstname || '' '' || mems.surname as member, facs.name as facility, case when mems.memid = 0 then bks.slots*facs.guestcost else bks.slots*facs.membercost end as cost from exercises.members mems inner join exercises.bookings bks on mems.memid = bks.memid inner join exercises.facilities facs on bks.facid = facs.facid where bks.starttime >= ''2012-09-14'' and bks.starttime < ''2012-09-15'' and ( (mems.memid = 0 and bks.slots*facs.guestcost > 30) or (mems.memid != 0 and bks.slots*facs.membercost > 30) ) order by cost desc;'),
(19, 'select distinct mems.firstname || '' '' || mems.surname as member, (select recs.firstname || '' '' || recs.surname as recommender from exercises.members recs where recs.memid = mems.recommendedby ) from exercises.members mems order by member;'),
(20, 'select member, facility, cost from ( select mems.firstname || '' '' || mems.surname as member, facs.name as facility, case when mems.memid = 0 then bks.slots*facs.guestcost else bks.slots*facs.membercost end as cost from exercises.members mems inner join exercises.bookings bks on mems.memid = bks.memid inner join exercises.facilities facs on bks.facid = facs.facid where bks.starttime >= ''2012-09-14'' and bks.starttime < ''2012-09-15'' ) as bookings where cost > 30 order by cost desc;');